package edu.hust.model;

public class DetailRecordForClass {

	private String studentName;
	private int sumOfRollCall;
	private int sumOfMissingRollCall;
	private int sumOfSpecialCase;
	private String email;
	private int sumOfLessons;
	
	public DetailRecordForClass() {
		super();
		// TODO Auto-generated constructor stub
	}

	public String getStudentName() {
		return studentName;
	}

	public void setStudentName(String studentName) {
		this.studentName = studentName;
	}

	public int getSumOfRollCall() {
		return sumOfRollCall;
	}

	public void setSumOfRollCall(int sumOfRollCall) {
		this.sumOfRollCall = sumOfRollCall;
	}

	public int getSumOfMissingRollCall() {
		return sumOfMissingRollCall;
	}

	public void setSumOfMissingRollCall(int sumOfMissingRollCall) {
		this.sumOfMissingRollCall = sumOfMissingRollCall;
	}

	public int getSumOfSpecialCase() {
		return sumOfSpecialCase;
	}

	public void setSumOfSpecialCase(int sumOfSpecialCase) {
		this.sumOfSpecialCase = sumOfSpecialCase;
	}

	public String getEmail() {
		return email;
	}

	public void setEmail(String email) {
		this.email = email;
	}

	public int getSumOfLessons() {
		return sumOfLessons;
	}

	public void setSumOfLessons(int sumOfLessons) {
		this.sumOfLessons = sumOfLessons;
	}
	
	
	
}
